sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    var itemGR = new GlideRecord('x_476139_ebay_item');
    itemGR.addQuery('state', 'awaiting_shipment');
    itemGR.query();
    while (itemGR.next()) {
        itemGR.setValue('state', '');
        itemGR.update();
    }
    var params = {
        "filter": "orderfulfillmentstatus:%7BNOT_STARTED%7CIN_PROGRESS%7D"
    };
    data.missingOrders = [];
    var orders = EbayUtil.getOrders(params);
    orders = orders.orders;
    for (var i = 0; i < orders.length; i++) {
        for (var j = 0; j < orders[i].lineItems.length; j++) {
            itemGR = new GlideRecord('x_476139_ebay_item');
            if (orders[i].lineItems[j].sku && itemGR.get('number', orders[i].lineItems[j].sku)) {
                itemGR.setValue('state', 'awaiting_shipment');
                itemGR.update();
            } else {
                itemGR.initialize();
                itemGR.addQuery('legacy_listing_id', orders[i].lineItems[j].legacyItemId).addOrCondition('name', orders[i].lineItems[j].title);
                itemGR.query();
                if (itemGR.next()) {
                    itemGR.setValue('state', 'awaiting_shipment');
                    itemGR.update();
                } else {
                    var order = {
                        "name": orders[i].lineItems[j].title
                    };
                    data.missingOrders.push(order);
                }
            }
        }
    }

	data.totalOrders = orders.length;

})();
