sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  var cogGR = new GlideRecord('x_476139_ebay_sourcing_trip');
  cogGR.addEncodedQuery('dateONThis year@javascript:gs.beginningOfThisYear()@javascript:gs.endOfThisYear()');
  cogGR.query();
  var totalAmount = 0;
  var totalItems = 0;

  while(cogGR.next()){
	var amount = parseFloat(cogGR.getValue('amount'));
	var items = parseInt(cogGR.getValue('number_of_items'), 10);
	totalAmount += amount;
	totalItems += items;
  }
  console.log('total amount: ' + totalAmount);
  console.log('total Items: ' + totalItems);

  var average = (totalAmount / totalItems).toFixed(2);
  data.average = average;

})();
