sys_script_fix.script:
var today = new GlideDate();
var endDate = today.getDisplayValue() + 'T00:00:00.000Z';
today.addDaysLocalTime(-1);
var startDate = today.getDisplayValue() + 'T00:00:00.000Z';
    var params = {
        "limit": 1000,
        "filter": "transactionType:%7BNON_SALE_CHARGE%7D",
        "filter": "transactionDate:%5B" + startDate + '..' + endDate + '%5D'
    };
    var transactions = EbayUtil.getPromotedListingFees(params);
    transactions = transactions.transactions;
    for (var i = 0; i < transactions.length; i++) {
        if (transactions[i].feeType && transactions[i].feeType == "AD_FEE") {
            for (var j = 0; j < transactions[i].references.length; j++) {
                if (transactions[i].references[j].referenceType == "ITEM_ID"){
                    var itemGR = new GlideRecord('x_476139_ebay_item');
                    if (itemGR.get('legacy_listing_id', transactions[i].references[j].referenceId)) {
                        itemGR.setValue('promoted_listing_fee', transactions[i].amount.value);
                        itemGR.update();
                    }
                }
            }
        }
    }
